import React, { useState } from "react";

import './AddUsers.css';

const AddUsers = props => {
    const [enteredUsername, setEnteredUsername] = useState('');
    const [enteredAge, setEnteredAge] = useState('');

    const usernameChangeHandler = event => {
        setEnteredUsername(event.target.value);
    }

    const ageChangeHandler = event => {
        setEnteredAge(event.target.value);
    }

    const formSubmitHandler = (event) => {
        event.preventDefault();
        if (enteredUsername.trim().length === 0 || enteredAge.trim().length === 0) {
            console.log("Missing information!");
        } else {
            const NewUser = {
                name: enteredUsername,
                age: enteredAge,
                id: Math.random().toString()
            };
            props.onSubmitAddUser(NewUser);
            setEnteredUsername('');
            setEnteredAge('');
        }
    }

    return (
        <form className="add-users" onSubmit={formSubmitHandler}>
            <div>
                <label>Username</label>
                <input type="text" value={enteredUsername} onChange={usernameChangeHandler} />
                <label>Age(Years)</label>
                <input type="number" min="1" value={enteredAge} onChange={ageChangeHandler} />
            </div>
            <button type="submit">Add User</button>
        </form >
    );
}

export default AddUsers;
