import React, { useState } from 'react';
import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import './Expenses.css';
import ExpensesFilter from './ExpensesFilter';

function Expenses(props) {
    const [filteredYear, setFilterYear] = useState('2021');

    const filterByYear = selectedYear => {
        setFilterYear(selectedYear);
        const filteredExpenses = props.items.filter(expense => expense.date.getFullYear() === selectedYear);
        console.log(filteredExpenses);
    };

    return (
        <Card className='expenses'>
            <ExpensesFilter selected={filteredYear} onSelectFilterByYear={filterByYear} />
            {props.items.map(expense => (
                <ExpenseItem
                    key={expense.id}
                    title={expense.title}
                    amount={expense.amount}
                    date={expense.date}
                />
            ))};
        </Card>
    )
}

export default Expenses
